import logo from "./logo512.png";
import cartImage from "./addtocart.png";
import React, { useState, useEffect } from "react";
import { Categories } from "./Categories"
import items from "./selected_products.json";
import checkmark from "./404-tick.png";

export const App = () => {
  console.log("Step 1: After reading file :");
  const [showMore, setShowMore] = useState(false);
  const [showCart, setShowCart] = useState(false);
  const [showPurchase, setshowPurchase] = useState(false);
  const [ProductsCategory, setProductsCategory] = useState(items);
  const [query, setQuery] = useState('');
  const [product, setProduct] = useState([]);
  const [state, setstate] = useState({
    query: '',
    list: []
  })
  
  const [paymentInfo, setPaymentInfo] = useState({
    name: '',
    email: '',
    card: '',
    address: '',
    city: '',
    state: '',
    zip: 0
  });
  //cart var
  const [cart, setCart] = useState([]);
  const [cartTotal, setCartTotal] = useState(0);
  const showAllItems = product.map((el) => (
    <div key={el._id}>
      <img src={el.IMG_LINK} width={30} /> <br />
      Title: {el.PRODUCT_NAME} <br />
      Category: {el.CATEGORY} <br />
      Price: {el.PRICE} <br />
      
    </div>
  ));

  function getAllProducts() {
    fetch("http://localhost:4000/")
      .then((response) => response.json())
      .then((data) => {
        console.log("Show Catalog of Products :");
        console.log(data);
        setProduct(data);
      });
    
  }
 
  //cart fumctions
  function howManyofThis(id) {
    let hmot = cart.filter((cartItem) => cartItem.id === id);
    return hmot.length;

  }
  function handleShowHideCheckout() {
    if (cart.length > 0) {
      setShowCart(!showCart);
    } else { window.alert("No items in cart"); }
  }
  function handleshowPurchase() {
    if (cart.length > 0) {
      setShowCart(!showCart);
    } else { window.alert("No items in cart"); }
  }
  function handleShowHideCart() {
    if ((cart.length > 0) || showMore) {
      setShowMore(!showMore);
    } else { window.alert("No items in cart"); }
  }
  useEffect(() => {
    total();
  }, [cart]);

  const total = () => {
    let totalVal = 0;
