      state.setAttribute("class", "form-control is-invalid");
      val = false;
    } else {
      state.setAttribute("class", "form-control is-valid");
      payInfo.state = state.value;
    }
    console.log(payInfo);
    setPaymentInfo(payInfo);
    return val;
